    private void bregisterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bregisterActionPerformed
        // TODO add your handling code here:
        String nama=Tnama.getText();
        String nik=Tnik.getText();
        String tempat=Ttempatlahir.getText();
        String agama=(String)cmbagama.getSelectedItem();
        String goldar=(String)cmbgoldar.getSelectedItem();
        String alamat=Talamat.getText();
        String kelamin;
        if(Rbpria.isSelected()){
            kelamin=Rbpria.getText();
        }
        if(Rbwanita.isSelected()){
            kelamin=Rbwanita.getText();
        }
        String rt=Trt.getText();
        String rw=Trw.getText();
        String kelurahan=(String)cmbkel.getSelectedItem();
        String kecamatan=(String)cmbkec.getSelectedItem();
        String kota=Tkota.getText();
        if(Rbpria.isSelected()){
            kelamin=Rbpria.getText();
        }
        else{
            kelamin=Rbwanita.getText();
        }
        String status;
        if(cbkawin.isSelected()){
            status=cbkawin.getText();
        }
        else{
            status=cbbelumkawin.getText();
        }
        String pekerjaan=Tpekerjaan.getText();
        String kewarganegaraan=null;
        if(cbwna.isSelected()){
            kewarganegaraan=cbwna.getText();
        }
        else{
            status=cbwni.getText();
        }
        Thasil.setText("\nNIK                           \t:"+nik+
                       "\nNama                          \t:"+nama+
                       "\nTempat/Tgl Lahir              \t:"+tempat+
                       "\nJenis Kelamin                 \t:"+kelamin+
                       "\nGolongan Darah                \t:"+goldar+
                       "\nAlamat                        \t:"+alamat+
                       "\nRT                            \t:"+rt+
                       "\nRW                            \t:"+rw+
                       "\nKelurahan                     \t:"+kelurahan+
                       "\nKecamatan                     \t:"+kecamatan+
                       "\nKab/Kota                      \t:"+kota+
                       "\nAgama                         \t:"+agama+
                       "\nStatus Perkawinan             \t:"+status+
                       "\nPekerjaan                     \t:"+pekerjaan+
                       "\nKewarganegaraan               \t:"+kewarganegaraan);
    }//GEN-LAST:event_bregisterActionPerformed

