    private void bresetActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bresetActionPerformed
        // TODO add your handling code here:
        Tnama.setText("");
        Tnik.setText("");
        Talamat.setText("");
        Ttempatlahir.setText("");
        Trt.setText("");
        Trw.setText("");
        cmbkel.setSelectedItem(null);
        cmbkec.setSelectedItem(null);
        Tkota.setText("");
        cmbgoldar.setSelectedItem(null);
        cmbagama.setSelectedItem(null);
        Rbpria.setSelected(false);
        Rbwanita.setSelected(false);
        cbwna.setSelected(false);
        cbwni.setSelected(false);
        Tpekerjaan.setText("");
        buttonGroup1.clearSelection();
        jk.clearSelection();
        buttonGroup2.clearSelection();
        Thasil.setText("");
    }//GEN-LAST:event_bresetActionPerformed

